<?phpclass Databases_Tables_Admin extends Zend_Db_Table{    protected $_name = 'admin';    var $admin_wechat_openid;    var $session_id;        function IsValid()    {    	$result = array();    	    	if($this->admin_wechat_openid)    	{    		$row = $this->fetchRow("admin_wechat_openid='".$this->admin_wechat_openid."' and admin_status=1");    		    		if(!empty($row))    		{    			$result = $row->toArray();    		}    	}    	    	return $result;    }        function GenerateSession()    {    	if($this->admin_wechat_openid)    	{    		$row = $this->fetchRow("admin_wechat_openid='".$this->admin_wechat_openid."'");    		    		$a = "lK2d3Hks3G87";    		$b = $row['admin_id'];    		$c = date("mYd");    		    		$result = sha1($a.$b.$c);    		    		//add into table    		$row->admin_session = $result;    		$row->save();    	}else{    		$result = "";    	}    	    	return $result;    }        function CheckSessionValidation()    {    	$result = array();    	    	if($_SESSION['admin_session'])    	{    		$row = $this->fetchRow("admin_session = '".$_SESSION['admin_session']."' and admin_status=1");    		    		if(!empty($row))    		{    			$a = "lK2d3Hks3G87";    			$b = $row['admin_id'];    			$c = date("mYd");    			    			$realtime_val = sha1($a.$b.$c);    			    			if($realtime_val === $_SESSION['admin_session'])    			{    				$result = $row->toArray();    			}    		}    	}    	    	return $result;    }}