<?phpclass Databases_Tables_Admin extends Zend_Db_Table{    protected $_name = 'admin';    var $admin_wechat_openid;            function IsValid()    {    	$result = array();    	    	if($this->admin_wechat_openid)    	{    		$row = $this->fetchRow("admin_wechat_openid='".$this->admin_wechat_openid."' and admin_status=1");    		    		if(!empty($row))    		{    			$result = $row->toArray();    		}    	}    	    	return $result;    }}