<?phpclass Databases_Tables_ErrorLog extends Zend_Db_Table{    protected $_name = 'error-log';    var $error_id;    var $note1;    var $note2;    var $log_time;    var $visitor_ip;            function AddLog()    {        $result = "";    	        if($this->error_id)        {        	$row = $this->createRow();        	$row->log_time = date("Y-m-d H:i:s");        	$row->error_id = $this->error_id;        	$row->note1 = $this->note1;        	$row->note2 = $this->note2;        	$row->visitor_ip = $this->visitor_ip;        	$row_id = $row->save();                	//read error msg        	$mod_error_definition = new Databases_Tables_ErrorDefinition();        	$result = $mod_error_definition->GetVal($this->error_id);        }                return $result;    }}